import React from 'react';

function Card(props){
return(
    <div>
      <h1 className="heading">My Contacts</h1>
      <div className="card">
        <div className="top">
          <h2>{props.name}</h2>
          <img
            src={props.imgURL}
          />
        </div>
        <div className="bottom">
          <p> {props.phone} </p>
          <p>b@beyonce.com</p>
        </div>
      </div>
    </div>
)
}



export default Card;
